for (let i of products.data) {
  //Create Card
  let card = document.createElement("div");
  //Card should have category and should stay hidden initially
  card.classList.add("card", i.category, "hide");
  //image div
  let imgContainer = document.createElement("div");
  imgContainer.classList.add("image-container");
  //img tag
  let image = document.createElement("img");
  image.setAttribute("src", i.image);
  imgContainer.appendChild(image);
  card.appendChild(imgContainer);
  //container
  let container = document.createElement("div");
  container.classList.add("container");
  //product name
  let name = document.createElement("h5");
  name.classList.add("product-name");
  name.innerText = i.title.toUpperCase();
  container.appendChild(name);
  //salary
  let salary = document.createElement("h6");
  salary.innerText = "$" + i.salary;
  container.appendChild(salary);
  //company
  
  let company = document.createElement("h6");
  company.innerText = "COMPAMY: " + i.company;
  container.appendChild(company);
////FAV
  let newButton = document.createElement('button');
  newButton.textContent = 'ADD to fav!';
  newButton.style.backgroundColor = 'black';
  newButton.style.color = 'white';
  card.appendChild(newButton);

  // Add a click event listener to the "ADD to fav!" button
  newButton.addEventListener('click', function() {
    // Add the selected card to favorites
    addToFavorites(i);
  });

  card.appendChild(container);
  document.getElementById("products").appendChild(card);
    //button
    let applyButton = document.createElement('button');
    applyButton.textContent = 'Apply' + '+';
    // Change the background color of the second button
    applyButton.style.backgroundColor = 'black';
    applyButton.style.color = 'white';
    
    // Add a click event listener to the button
    applyButton.addEventListener('click', function() {
        // Redirect to the new page
        window.location.href = 'applyforjob.html';
    });
    
    // Append the button to the container
    container.appendChild(applyButton); 
}

function addToFavorites(selectedCard) {
  // Get existing favorites from local storage
  let favorites = JSON.parse(localStorage.getItem('favorites')) || [];

  // Check if the selected card is not already in favorites
  if (!favorites.some(card => card.title === selectedCard.title)) {
    // Add the selected card to favorites
    favorites.push(selectedCard);

    // Update local storage with the new favorites list
    localStorage.setItem('favorites', JSON.stringify(favorites));

    // You can redirect to another HTML page here if needed
    // window.location.href = 'Save job.html';
  } else {
    alert('This card is already in your favorites!');
  }
}


//parameter passed from button (Parameter same as category)
function filterProduct(value) {
  //Button class code
  let buttons = document.querySelectorAll(".button-value");
  buttons.forEach((button) => {
    //check if value equals innerText
    if (value.toUpperCase() == button.innerText.toUpperCase()) {
      button.classList.add("active");
    } else {
      button.classList.remove("active");
    }
  });

  //select all cards
  let elements = document.querySelectorAll(".card");
  //loop through all cards
  elements.forEach((element) => {
    //display all cards on 'all' button click
    if (value == "all") {
      element.classList.remove("hide");
    } else {
      //Check if element contains category class
      if (element.classList.contains(value)) {
        //display element based on category
        element.classList.remove("hide");
      } else {
        //hide other elements
        element.classList.add("hide");
      }
    }
  });
}

//Search button click
document.getElementById("search").addEventListener("click", () => {
  //initializations
  let searchInput = document.getElementById("search-input").value;
  let elements = document.querySelectorAll(".product-name");
  let cards = document.querySelectorAll(".card");

  //loop through all elements
  elements.forEach((element, index) => {
    //check if text includes the search value
    if (element.innerText.includes(searchInput.toUpperCase())) {
      //display matching card
      cards[index].classList.remove("hide");
    } else {
      //hide others
      cards[index].classList.add("hide");
    }
  });
});

//Initially display all products
window.onload = () => {
  filterProduct("None");
};